package PracticaDiaria;

/**
 * @author david
 * Ejercicio 1) Declara dos variables numéricas (con el valor que desees), muestra por consola la suma, resta, multiplicación, división y módulo (resto de la división).
 */
public class Ej1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			//declaro dos variables numericas con el valor que sea
			int numero1 = 15;
			int numero2 = 38;
			//muestro por pantalla todos los resultados de las operaciones
			System.out.println("El resultado de la suma es: " + (numero1 + numero2));
			System.out.println("El resultado de la resta es: " + (numero1 - numero2));
			System.out.println("El resultado de la multiplicacion es: "+ (numero1 * numero2));
			System.out.println("El resultado de la division es: "+ (numero1 / numero2));

	}

}
