package PracticaDiaria;

/**
 * @author david
 *10) Muestra los números del 1 al 100 (ambos incluidos). Usa un bucle while.
 */
public class Ej10 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//declaramos el numero para que empiece desde 1
		int numero=1;
		//definimos el bucle, incluyendo el 100 
		
		while(numero<=100) {
			System.out.println(numero);
			//pintamos el numero incrementando de 1 en 1
			numero++;
			
		}
		
	}

}
